let ParseRlue = $.require("ParseRlue");

function toRedirectTreating(config) {
    return $.require("redirect").doubleTreating(config);
}

function toRedirect(aliUrl, sharePwd) {
    return $.require("redirect").getModeUrl(aliUrl, sharePwd);
}

function getCompleteRule(rule) {
    if (rule.ruleType === "JSON") {
        let find = rule.find;
        rule.find = (s, page) => {
            let url = String(rule.basicUrl).replace("**", encodeURIComponent(s)).replace("fypage", page + "");
            return ParseRlue.parseSearchJson(url, find);
        }
    } else if (rule.ruleType === "HTML") {
        let find = rule.find;
        rule.find = (s, page) => {
            let url = String(rule.basicUrl).replace("**", encodeURIComponent(s)).replace("fypage", page + "");
            return ParseRlue.parseSearchHtml(url, find);
        }
    } else {
        let find = new Function("s", "page", "user", "basicUrl", rule.find);
        rule.find = (s, page) => find(s, page, rule.user || {}, rule.basicUrl || "");
        /*if (rule.findAliUrl) {
            rule.findAliUrl = new Function("input", "basicUrl", rule.findAliUrl);
        }*/
    }
    return rule;
}
$.exports.carryRule = function(rule, s, page, searchMode) {
    rule = getCompleteRule(rule);
    let list = rule.find(s, page);
    if (!list) return [];

    let descTips = "â€œâ€œâ€â€" + rule.name.fontcolor("#f13b66a") + "&nbsp;";
    let resList = [];

    for (let it of list) {
        if (it.skip) {
            resList.push(it);
            continue;
        }
        if (searchMode && !searchContains(it.title, s, true)) continue;
        if (rule.findAliUrl) {
            it.url = $(it.url).lazyRule(toRedirectTreating, {
                findAliUrl: rule.findAliUrl,
                basicUrl: rule.basicUrl,
                name: rule.name,
                author: rule.author,
                aliUrl: it.url,
                ruleType: rule.ruleType
            });
        } else {
            it.url = $("#noLoading#").lazyRule(toRedirect, it.url, it.sharePwd || "");
        }
        let title = it.title || "";
        resList.push({
            title: title.includes("â€œâ€œâ€â€") ? title : "â€œâ€œâ€â€" + title.replace(new RegExp("<em>|</em>", "g"), "").replace(new RegExp("(" + s + ")", "gi"), "<strong><span style=\"color: #1E90FF\">$1</span></strong>"),
            desc: descTips + (it.desc || ""),
            url: it.url,
            pic_url: it.pic_url,
            col_type: it.pic_url ? "movie_1_vertical_pic" : "text_1",
            extra: Object.assign({
                inheritTitle: false,
                noPic: true
            }, it.extra)
        });
    }
    return resList;
}
$.exports.carrySRule = function(rule, s, page, searchMode) {
    rule = getCompleteRule(rule);
    let list = rule.find(s, page);
    if (!list) return [];
    let resList = [];
    for (let it of list) {
        if (it.skip) {
            resList.push(it);
            continue;
        }
        if (searchMode && !searchContains(it.title, s, true)) continue;
        if (rule.findAliUrl) {
            it.url = $(it.url).lazyRule(toRedirectTreating, {
                findAliUrl: rule.findAliUrl,
                basicUrl: rule.basicUrl,
                name: rule.name,
                author: rule.author,
                aliUrl: it.url,
                ruleType: rule.ruleType
            });
        } else {
            it.url = $("#noLoading#").lazyRule(toRedirect, it.url, it.sharePwd || "");
        }
        resList.push({
            title: it.title,
            desc: it.desc || "",
            url: it.url,
            pic_url: it.pic_url,
            extra: Object.assign({
                inheritTitle: false,
                noPic: true
            }, it.extra)
        });
    }
    return resList;
}
let size = 10;

let rules;

function getRules(group) {
    let rulesTmp;
    if (!rules) {
        rules = $.require("configs").getUsefulJson();
    }
    if (group) {
        rulesTmp = rules.filter(v => v.group === group);
    } else {
        rulesTmp = rules;
    }
    return rulesTmp;
}
$.exports.getAllData = function(group) {
    return getRules(group);
};
$.exports.getData = function(page, rule, group) {
    if (rule) {
        if (page != null && page > 1) {
            return getRules().filter(i => i.name == rule && i.page);
        }
        return getRules().filter(i => i.name == rule);
    }
    let rulesTmp = getRules(group);
    let start = (page - 1) * size;
    if (rulesTmp.length < start + 1) {
        return [];
    }
    let len = size;
    if (rulesTmp.length < page * size) {
        len = rulesTmp.length - start;
    }
    return rulesTmp.slice(start, start + len);
};
$.exports.getNames = function(group) {
    return getRules(group).map(it => [it.name, it.basicUrl]);
};

$.exports.getGroupNames = function() {
    return Array.from(new Set(getRules().map(v => v.group))).filter(v => v);
};
$.exports.findModulo = function(input) {
    let oriMods = [2, 3];
    let mods = oriMods.map(v => input % v);
    let zeroMods = mods.indexOf(0);

    if (zeroMods > -1) {
        return oriMods[zeroMods];
    } else {
        return oriMods[mods.indexOf(Math.max.apply(null, mods))];
    }
};

