            "key_scanParam": json
        })) {
        createQRCode(qr.content.data.codeContent, false);
    }
    return qr;
}

function backHome(t, i) {
    back(i);
    toast(t);
}

function fromAppGetToken(qr) {
    let qr = getQRCode();
    if (!qr) return backHome("è·å–å¤±è´¥");
    let ckk = qr.content.data;
    let body = {
        ck: ckk.ck,
        t: ckk.t + "",
        appName: "aliyun_drive",
        appEntrance: "web",
        isMobile: false,
        lang: "zh_CN",
        navPlatform: "MacIntel",
        returnUrl: "",
        navlanguage: "zh-CN",
        fromSite: 52,
        bizParams: ""

    };
    /*
     {
        NEW: "å¾…æ‰«æ",
        CANCELED: "å–æ¶ˆ",
        SCANED:"å·²æ‰«æ",
        CANCELED: "å®Œæˆ"
    }*/
    let errorCount = 0;
    for (let i = 0; i < 30; i++) {
        try {
            let result = JSON.parse(post("https://passport.aliyundrive.com/newlogin/qrcode/query.do?appName=aliyun_drive&fromSite=52&_bx-v=2.2.3", {
                body: body
            }));
            let qrCodeStatus = result.content.data.qrCodeStatus;

            if (qrCodeStatus === "CONFIRMED") {
                if (result.content.data.bizExt) {
                    let info = JSON.parse(base64Decode(result.content.data.bizExt));
                    let aliTokensFilepath = $.require("hiker://page/tokenPath").aliTokensFilepath;
                    let aliToken = {
                        refresh_token: info.pds_login_result.refreshToken
                    };
                    writeFile(aliTokensFilepath, JSON.stringify(aliToken));
                    addItemAfter("loginy", {
                        title: "ç™»å½•æˆåŠŸ(ç‚¹å‡»è¿”å›)",
                        url: $("#noLoading#").lazyRule(() => {
                            back(false);
                            return "hiker://empty";
                        }),
                        col_type: "text_center_1",
                    });
                    backHome("ç™»å½•æˆåŠŸ", true);
                } else {
                    backHome("ç™»å½•å¤±è´¥,è¯·å°è¯•å…¶ä»–çš„æ–¹æ³•");
                }
                return;
            } else if (qrCodeStatus === "CANCELED") {
                backHome("å–æ¶ˆç™»å½•æˆæƒ");
                return;
            }
            if (["NEW", "SCANED"].includes(qrCodeStatus)) {
                java.lang.Thread.sleep(2500);
            } else {
                backHome("è¶…æ—¶");
                return;
            }
        } catch (e) {
            errorCount++;
            if (errorCount > 4) {
                throw e;
            }
        }
    }

}
