$.exports = {
    checkali(realurl) {
        try {
            if (realurl.includes("com/s/") && realurl != "") {
                let shareId = realurl.split("?")[0].split("/")[4];
                let share = fetch("https://api.aliyundrive.com/adrive/v3/share_link/get_share_by_anonymous", {
                    headers: {
                        'content-type': 'application/json',
                        "User-Agent": PC_UA,
                        "Referer": "https://www.aliyundrive.com/"
                    },
                    body: {
                        "share_id": shareId
                    },
                    method: 'POST'
                });
                if (share.includes("share_link is") || share.includes("share_link cannot")) return false;
            }
        } catch (e) {}
        return true;
    },
    bytesToSize(size) {
        if (!size) {
            return '0';
        }
        const units = ['B', 'KB', 'MB', 'GB', 'TB', 'PB', 'EB', 'ZB', 'YB'];
        let i = 0;
        while (size >= 1024) {
            size /= 1024;
            i++;
        }
        size = i ? Number(size.toFixed(2)) : size;
        return `${size} ${units[i]}`;
    },
    getCryptoJS() {
        if (typeof CryptoJS === "undefined") {
            eval(getCryptoJS());
            return CryptoJS;
        }
        return CryptoJS;
    },
    matchAliUrl(text, isG) {
        return $.require("alitest").matchAliUrl(text, isG);
    },
    getIPv4Address() {
        const NetworkInterface = java.net.NetworkInterface;
        try {
            for (let en = NetworkInterface.getNetworkInterfaces(); en.hasMoreElements();) {
                let intf = en.nextElement();
                for (let enumIpAddr = intf.getInetAddresses(); enumIpAddr.hasMoreElements();) {
                    let inetAddress = enumIpAddr.nextElement();
                    if (!inetAddress.isLoopbackAddress() && inetAddress instanceof java.net.Inet4Address) {
                        return String(inetAddress.getHostAddress());
                    }
                }
            }
        } catch (ex) {
            log(ex.toString());
        }
        return "0.0.0.0";
    }
}

