const path = "hiker://files/rules/LoyDgIk/aliSimpleRules.json";
let data;

function getJson() {
    if (Array.isArray(data)) {
        return data;
    }
    try {
        data = JSON.parse(readFile(path) || "[]");
    } catch (e) {
        deleteFile(path);
        data = [];
    }
    return data;
}

function saveJson(json) {
    saveFile(path, JSON.stringify(json));
}

function getUsefulJson() {
    return getJson().filter(v => !v.forbidden);
}

function getForbiddenJson() {
    return getJson().filter(v => v.forbidden);
}

function getGroupJson(group) {
    return getJson().filter(v => v.group === group);
}

function getGroupNames() {
    return Array.from(new Set(getJson().map(v => v.group))).filter(v => v);
}

if (!fileExist(path) && MY_RULE) {
    let rules = $.require("rules");
    $.hiker.window = {};
    eval.call(null, fetch('hiker://assets/beautify.js'));
    rules = rules.map((v) => {
        v.find = window.js_beautify(v.find.toString().replace(/function.*?\(s.*?page\).*?\{/, "").replace(/(\}[\s\S].*?)$/, ""));
        if (v.findAliUrl) {
            v.findAliUrl = window.js_beautify(v.findAliUrl.toString().replace(/function.*?\(input\).*?\{/, "").replace(/(\}[\s\S].*?)$/, ""));
        }
        return v;
    });
    saveJson(rules);
    toast("å·²åˆå§‹åŒ–");
}
$.exports = {
    getJson,
    saveJson,
    getUsefulJson,
    getForbiddenJson,
    path,
    getGroupNames,
    getGroupJson
};

