$.exports = [{
        name: "å°äº‘",
        page: true,
        find(s, page) {
            let basicUrl = "https://www.yunso.net";
            let api = "/api/validate/search?wd=" + encodeURIComponent(s) + "&mode=90001&stype=20100&uk=&page=" + page + "&limit=20&screen_filetype=undefined";
            let json = post(basicUrl + api, {
                body: "data=" + api,
                headers: {
                    Referer: "https://www.yunso.net/index/user/s?wd=" + encodeURIComponent(s)
                }
            });
            let html = JSON.parse(json).data;
            let list = pdfa(html, "body&&.layui-card:has(a)");
            let d = [];
            for (let item of list) {
                let title = pdfh(item, "a&&Text").replace("#æ–‡ä»¶å¤¹ ", "");
                //let time = /\d{4}(-\d{2}){2} \d{2}(\:\d{2}){2}/.exec(pdfh(item, ".layui-card-body&&Text")) || [];
                d.push({
                    title: title,
                    //desc: time[0],
                    url: base64Decode(pdfh(item, "a&&url"))
                });
            }
            return d;
        }
    }, {
        name: "æ··åˆç›˜",
        page: true,
        find(s, page) {
            let url = "https://api.hunhepan.com/v1/search";
            let json = post(url, {
                headers: {
                    Referer: "https://hunhepan.com/"
                },
                body: JSON.stringify({
                    "q": s,
                    "page": page,
                    "size": 15
                })
            });
            json = JSON.parse(json);
            let list = json.data.list;
            let d = [];
            for (let i = 0; i < list.length; i++) {
                let it = list[i];
                if (it.disk_type !== "ALY" || it.share_user === "ä¸¶***ä¸¶") continue;
                d.push({
                    title: it.disk_name,
                    pic_url: pd(it, "img&&src", url),
                    url: it.link,
                    desc: it.update_time + "\n" + it.link,
                });
            }
            return d;
        }
    }, {
        name: "èµ„æºå¸®",
        page: true,
        find(s, page) {
            function search() {
                let rtnList = [];
                let resp = request("https://zyb.upyunso.com/v15/search?keyword=" + en_aes(s) + "&s_type=2&page=" + page + "&atk=&did=" + generateRandomHexString(64), {
                    headers: {
                        "user-agent": "Mozilla/5.0 (Linux; Android 10; Redmi Note 7 Build/QKQ1.190910.002; wv) AppleWebKit/537.36 (KHTML, like Gecko) Version/4.0 Chrome/81.0.4044.138 Mobile Safari/537.36 uni-app Html5Plus/1.0 (Immersed/29.09091)",
                    },
                });
                let respData = de_aes(resp);
                let jsonData = JSON.parse(respData) || {};
                let result = jsonData.result || {};
                let items = result.items || [];

                for (let i = 0; i < items.length; i++) {
                    let item = items[i];
                    let content = item.content || [];
                    if (content.length == 0 || !item.page_url) continue;
                    let url = de_aes(item.page_url) || "";
                    if (!url) continue;
                    rtnList.push({
                        title: (item.title.length > 30 ? item.title.slice(0, 27) + "..." : item.title) || "",
                        desc: "åˆ†äº«æ—¶é—´ï¼š" + item.insert_time,
                        url: url
                    });
                }

                return rtnList;
            }

            function generateRandomHexString(length) {
                let result = "";
                const characters = "0123456789abcdef";
                const charactersLength = characters.length;
                for (let i = 0; i < length; i++) {
                    result += characters.charAt(Math.floor(Math.random() * charactersLength));
                }
                return result;
            }
            let aliUtil = $.require("aliUtil");
            let CryptoJS = aliUtil.getCryptoJS();

