function toPath(value) {
    if (typeof value !== 'string') {
        return [];
    }
    let path = [];
    let current = '';
    let inBrackets = false;
    let escape = false;
    for (let i = 0; i < value.length; i++) {
        let char = value[i];
        if (escape) {
            current += char;
            escape = false;
        } else if (char === '\\') {
            escape = true;
        } else if (char === '.' && !inBrackets) {
            path.push(current);
            current = '';
        } else if (char === '[') {
            inBrackets = true;
        } else if (char === ']') {
            inBrackets = false;
        } else {
            current += char;
        }
    }
    if (current) {
        path.push(current);
    }
    return path;
}

function getJson(obj, path) {
    if (!obj || !Array.isArray(path)) {
        return undefined;
    }
    if (path.length === 0) {
        return obj;
    }
    let current = obj;
    for (let i = 0; i < path.length; i++) {
        let key = path[i];

        if (current === null || current === undefined || typeof current !== 'object') {
            return undefined;
        }
        current = current[key];
    }
    return current;
}

function dealRule(json, rule, basicUrl) {
    if (!rule) return;
    rule = rule.split("@js=");
    let rule0 = rule.at(0);
    let t = "";
    if (rule0) {
        t = getJson(json, toPath(rule0));
    } else {
        t = json;
    }
    if (rule.length > 1) {
        return new Function("input", "$$", "basicUrl", '"use strict";return ' + rule.at(1))(t, json, basicUrl);
    }
    return t;
}

function parseSearchJson(url, rule) {
    let json = JSON.parse(fetch(url));
    let rules = rule.split("\n");
    let list = getJson(json, toPath(rules.at(0)));
    if (!list || !Array.isArray(list)) {
        throw new Error("JSON:åˆ—è¡¨è§£æä¸ºç©º");
    }
    let data = [];
    for (let it of list) {
        data.push({
            title: dealRule(it, rules.at(1), url),
            url: dealRule(it, rules.at(2), url),
            desc: dealRule(it, rules.at(3), url),
            pic_url: dealRule(it, rules.at(4), url),
        });
    }
    return data;
}

function parseUrlJson(url, rule, basicUrl) {
    if (rule.startsWith("@js=")) {
        return dealRule(url, rule, basicUrl);
    } else {
        let json = JSON.parse(fetch(url));
        return dealRule(json, rule, basicUrl);
    }

}

function dealRuleForHtml(html, rule, basicUrl) {
    if (!rule) return;
    rule = rule.split("@js=");
    let rule0 = rule.at(0);
    let t = "";
    if (rule0) {
        t = pdfh(html, rule0);
    } else {
        t = html
    }
    if (rule.length > 1) {
        return new Function("input", "$$", "basicUrl", '"use strict";return ' + rule.at(1))(t, html, basicUrl);
    }
    return t;
}

function parseSearchHtml(url, rule) {
    let html = fetch(url);
    let rules = rule.split("\n");
    let list = pdfa(html, rules.at(0));
    if (!list || !Array.isArray(list)) {
        throw new Error("HTML:åˆ—è¡¨è§£æä¸ºç©º");
    }
    let data = [];
    for (let it of list) {
        let curl = dealRuleForHtml(it, rules.at(2), url);
        data.push({
            title: dealRuleForHtml(it, rules.at(1), url),
            url: curl.startsWith("http") ? curl : (getHome(url) + curl),
            desc: dealRuleForHtml(it, rules.at(3), url),
            pic_url: dealRuleForHtml(it, rules.at(4), url),
        });
    }
    return data;
}

function parseUrlHtml(url, rule, basicUrl) {
    if (rule.startsWith("@js=")) {
        return dealRuleForHtml(url, rule, basicUrl);
    } else {
        let html = fetch(url);
        return dealRuleForHtml(html, rule, basicUrl);
    }
}
$.exports = {
    parseSearchJson,
    parseUrlJson,
    parseSearchHtml,
    parseUrlHtml
};

