function aliM3u8Proxy(fileId, aliToken, isBackupDrivid, title) {
    let suffix = "media.m3u8";
    let urlname = "url";
    let cacheFilePath = "hiker://files/_cache/" + fileId + MY_PARAMS.timestamp[0]+ ".m3u8"
    //è¿‡æœŸè·å–æ’­æ”¾åŸå§‹é“¾æ¥
    function getAliPlayUrl(fileId, line) {
        let aliurl;
        try {
            const AliAccessApi = $.require("hiker://page/aliPlayApi?rule=" + title);
            let aliAccessApi = AliAccessApi.createDefault(null, null, null, aliToken, null, title).setMyDrividType(isBackupDrivid);
            let playUrlList =  aliAccessApi.getVideoCloudPreview(fileId) || [];

            aliurl = playUrlList.find(v => v.template_id == line)[urlname];
        } catch (e) {
            log(e.toString());
            throw e;
        }
        return aliurl;
    }

    function getM3u8Content(playurl) {
        //media.m3u8
        let home = playurl.split(suffix)[0];
        let f = fetch(playurl, {
            headers: {
                "User-Agent": PC_UA,
                "Referer": "https://www.aliyundrive.com/"
            },
            timeout: 3000
        }).split("\n");
        let ff = f.map(it => {
            if (it.startsWith("media-")) {
                return "/proxy?url=" + base64Encode(home + it)+"&timestamp="+MY_PARAMS.timestamp[0];
            }
            return it;
        }).join("\n");

        writeFile(cacheFilePath, ff);
        return ff;
    }

    function updateTsUrl(url) {
        let fid = url.split('&f=')[1].split('&')[0];
        //log('sfid-' + fid);
        let f = fetch(cacheFilePath).split("\n");
        f.forEach(it => {
            if (it && it.startsWith('/proxy?url=')) {
                let furl = base64Decode(it.replace('/proxy?url=', '').split("&")[0]);
                if (url.substr(url.indexOf('/media-'), url.indexOf('.ts')) == furl.substr(furl.indexOf('/media-'), furl.indexOf('.ts'))) {
                    url = furl;
                }
            }
        })
        let expires = url.split('x-oss-expires=')[1].split('&')[0];
        const lasttime = parseInt(expires) - Date.now() / 1000;
        if (lasttime < 60) {
            //log('è¿‡æœŸæ›´æ–°')
            let line = url.split('/media')[0]; //å–ä¹‹å‰æ’­æ”¾çš„tsæ®µçº¿è·¯
            line = line.substring(line.lastIndexOf('/') + 1);
            let f = getM3u8Content(getAliPlayUrl(fid, line)).split("\n");
            f.forEach(it => {
                if (it && it.startsWith('/proxy?url=')) {
                    let furl = base64Decode(it.replace('/proxy?url=', ''));
                    if (url.substr(url.indexOf('/media-'), url.indexOf('.ts')) == furl.substr(furl.indexOf('/media-'), furl.indexOf('.ts'))) {
                        url = furl;
                    }
                }
            })

        }
        return url;
    }
    try {
        let url = base64Decode(MY_PARAMS.url[0]);
        if (url.includes(".ts")) {
            return JSON.stringify({
                statusCode: 302,
                headers: {
                    "Location": updateTsUrl(url),
                    "User-Agent": PC_UA,
                    "Referer": "https://www.aliyundrive.com/"
                }
            });
        } else {
            let templateId = MY_PARAMS.templateId[0];
            if(fileExist(cacheFilePath)){
                return getM3u8Content(getAliPlayUrl(fileId, templateId));
            }else{
                return getM3u8Content(url);
            }
        }
    } catch (e) {
        log("#ä»£ç†æ’­æ”¾å¤±è´¥#\né”™è¯¯ä¿¡æ¯>" + e.toString() + "\né”™è¯¯è¡Œ>" + e.lineNumber);
    }
}
