function OpenApi($this, postApi) {
    this.$this = $this;
}

// ç”Ÿæˆ unique_id
function generateUUID() {
    return 'xxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxx'.replace(/[x]/g, function() {
        return (Math.random() * 16 | 0).toString(16);
    });
}


// ç”Ÿæˆ wifimac
function generateWifiMac() {
    const min = 10 ** 11;
    const max = 10 ** 12 - 1;
    return Math.floor(Math.random() * (max - min + 1)) + min;
}
function h(charArray, modifier) {
    // å»é™¤é‡å¤å­—ç¬¦
    var uniqueChars = [];
    for (var i = 0; i < charArray.length; i++) {
        if (uniqueChars.indexOf(charArray[i]) === -1) {
            uniqueChars.push(charArray[i]);
        }
    }

    var numericModifier = parseInt(modifier.slice(7));
    var transformedString = '';

    for (var j = 0; j < uniqueChars.length; j++) {
        var c = uniqueChars[j];
        var code = Math.abs(c.charCodeAt(0) - (numericModifier % 127) - 1);
        transformedString += String.fromCharCode(code < 33 ? code + 33 : code);
    }

    return transformedString;
}
