function decrypt(ciphertext, iv, t, unique_id, wifimac) {
    try {
        var key = generateKey(t, unique_id, wifimac);
        /*var cipherParams = CryptoJS.lib.CipherParams.create({
            ciphertext: CryptoJS.enc.Base64.parse(ciphertext)
        });

        var decrypted = CryptoJS.AES.decrypt(cipherParams, CryptoJS.enc.Utf8.parse(key), {
            iv: CryptoJS.enc.Hex.parse(iv),
            mode: CryptoJS.mode.CBC,
            padding: CryptoJS.pad.Pkcs7
        });

        var dec = decrypted.toString(CryptoJS.enc.Utf8);*/
        const CryptoUtil = $.require("hiker://assets/crypto-java.js");
        let keydata = CryptoUtil.Data.parseUTF8(key);
        let ivdata = CryptoUtil.Data.parseHex(iv);
        let encrypted0 = CryptoUtil.AES.decrypt(ciphertext, keydata, {
            mode: "AES/CBC/PKCS7Padding",
            iv: ivdata
        });
        return encrypted0.toString();
    } catch (error) {
        log("Decryption failed "+error.toString());
        throw error;
    }
}

function getParams(t, unique_id, wifimac) {
    let params = {
        'akv': '2.8.1496',
        'apv': '1.3.6',
        'b': 'OnePlus',
        'd': unique_id,
        'm': 'PJX110',
        'mac': '',
        'n': 'PJX110',
        't': String(t),
        'wifiMac': String(wifimac),
    };
    return params;
}
function generateKey(t, unique_id, wifimac) {
    var params = getParams(t, unique_id, wifimac);
    var sortedKeys = Object.keys(params).sort();

    var concatenatedParams = '';
    for (var i = 0; i < sortedKeys.length; i++) {
        var key = sortedKeys[i];
        if (key !== 't') {
            concatenatedParams += params[key];
        }
    }

    var hashedKey = h(concatenatedParams.split(''), t);
    return md5(hashedKey);
}
Object.assign(OpenApi.prototype, {
    getOpenTokenCode() {
        function get_qrcode_code(sid) {
            let res = fetch("https://openapi.aliyundrive.com/oauth/qrcode/" + sid + "/status");
            return JSON.parse(res).authCode;
        }

        let res = post("http://api.extscreen.com/aliyundrive/qrcode", {
