                "scopes": 'user:base,file:all:read,file:all:write',
                "width": 500,
                "height": 500,
            }),
        });
        let data = JSON.parse(res).data;
        let sid = data["sid"];
        let body = {
            "authorize": 1,
            "scope": "user:base,file:all:read,file:all:write",
            "drives": ["backup", "resource"],
            "scopes": ["user:base", "file:all:read", "file:all:write"],
            "sid": sid
        }
        let headers = {
            'authorization': this.$this.reaccessTk
        };
        let res2 = this.$this.postApi("https://open.aliyundrive.com/oauth/users/qrcode/authorize?sid=" + sid).body(body).headers(headers).errorWithRetry((e, _, http) => {
            this.$this.trySolve(e);
            http.mergeHeadAndBody({
                headers: {
                    'authorization': this.$this.reaccessTk,
                }
            });
            return true;
        }).start(true);
        return get_qrcode_code(sid);
    },
    getToken(body) {

        let timestamp = String(JSON.parse(fetch("http://api.extscreen.com/timestamp")).data.timestamp);
        let unique_id = generateUUID();
        let wifimac = generateWifiMac().toString();
        let res = post("http://api.extscreen.com/aliyundrive/v3/token", {
            body: JSON.stringify(body),
            headers: Object.assign({

                "token": "6733b42e28cdba32",
                'User-Agent': 'Mozilla/5.0 (Linux; U; Android 9; zh-cn; SM-S908E Build/TP1A.220624.014) AppleWebKit/533.1 (KHTML, like Gecko) Mobile Safari/533.1',
                //'Host': 'api.extscreen.com'
            }, getParams(timestamp, unique_id, wifimac))
        });
        let data = JSON.parse(res);
        let {
            iv,
            ciphertext
        } = data.data;
        return JSON.parse(decrypt(ciphertext, iv, timestamp, unique_id, wifimac));
    },
    getOpenToken() {
        return this.getToken({
            code: this.getOpenTokenCode(),
            grant_type: "authorization_code"
        });
    },
    refreshOpenToken() {
        return this.getToken({
            grant_type: "refresh_token",
            refresh_token: this.$this.openTokens.RefreshTokenOpen,
        });
    }
});
