function awardView(id, name) {
    setPageTitle(name);
    let s = getDoubanRes("https://frodo.douban.com/api/v2/ceremony/" + id);
    let a = [];
    a.push({
        title: 'â€œâ€œâ€â€' + (s.title + '(' + s.year + ')').big().bold(),
