    if (config[name] == null) {
        let defaultConfigs = $.require("hiker://page/defaultConfigs");
        config[name] = defaultConfigs[name] ? defaultConfigs[name] : {};
        writeFile(getMyVar('qdb_config'), JSON.stringify(config));
    }
    return config;
}

