function getConfig(name, rootConfig) {
    let config = rootConfig ? rootConfig : JSON.parse(fetch(getMyVar('qdb_config')));
    if (name) {
        if (config[name] == null) {
            config = initConfigs(name, config);
        }
        return config[name];
    } else {
        return config;
    }
}

