$('#noLoading#'+input).lazyRule(() => {
    try {
        showLoading('Loading');
        var play_url_json = JSON.parse(request(input)).data;
        let mid = play_url_json.video_library_id;
        let play_urls = JSON.parse(request('https://mod-api.xinpianchang.com/mod/api/v2/media/'+mid+'?appKey=61a2f329348b3bf77')).data.resource.progressive;
        // å¼€å§‹è·å–å¼¹å¹•
        let danmu = 'https://oss-danmaku12.xpccdn.com/' + mid + '.txt';
        let code = request(danmu);
        let arr = code.split("\n");
        let r = []
        for (let i = 0; i < arr.length; i++) {
            try {
                var line = JSON.parse(arr[i])
                if (line.content) {
                    r.push({
                        text: line.content,
                        time: line.offset
                    })
                }
            } catch (e) {}
        }
        writeFile("hiker://files/cache/danmu.json", JSON.stringify(r))
        // ç»“æŸå¼¹å¹•è·å–
        let urls = [];
        let names = [];
        let head = [];
        for (let i in play_urls) {
            urls.push(play_urls[i].url)
            names.push(play_urls[i].profile)
            head.push({'User-Agent': 'ExoPlayerLib'})
        }
        let all_url = JSON.stringify({
            urls: urls,
            names: names,
            danmu: "hiker://files/cache/danmu.json",
            headers: head
        });
        hideLoading();
        return all_url
    } catch (e) {
        let url = input.split('url=')[1]
        url = unescape(url)
        return 'web://' + url
    }
})

