    config = JSON.parse(_cfg)
}

// æ­£æ–‡è§£æé€šç”¨å˜é‡
let assignLazyRule = $.toString((QING_TITLE, title) => {
    let RULE = $.require("hiker://page/dataLoad?rule=" + QING_TITLE)().find((v) => v.title == title)
    return { pages: RULE.pages, pageList: JSON.parse(RULE.pages || '[]') }
}, QING_TITLE, RULE.title)
function genMyRule() {
    let rule = Object.assign({}, MY_RULE)
    delete rule.last_chapter_rule
    delete rule.preRule
    delete rule.pageList
    delete rule.pages
    return rule
}
function runCode(rule) {
    try {
        let [input, code] = rule.split('.js:')
        return code ? eval(code) : rule
    } catch (e) { return rule }
}
function genExUrl(url) {
    url = url.split(';')
    return url.shift() + ';' + paramHandle(url, thisRULE.ua, MY_RULE.url)
}
// æ­£æ–‡è§£æ
if (MY_RULE.find_rule.startsWith('js:')) {
    setResult = function(el, param1, param2, param3) {
        param1 = CALLBACK_KEY
        param2 = MY_RULE
        param3 = MY_TYPE
        if (Array.isArray(el.data)) el = el.data
        Array.prototype.push.apply(myCollection_el, el.map((v) => {
            let MY__RULE = Object.assign(genMyRule(), { params: v.extra || {} })
            v.col_type = v.col_type || MY_RULE.col_type

            if (!v.url || ['rule', 'pics', 'toast', 'input', 'copy', 'editFile', 'x5', 'x5WebView', 'x5Play', 'web', 'select', 'x5Rule', 'webRule', 'download', 'share'].find((vv) => v.url.startsWith(vv + '://')) || ['x5_webview_single', 'input'].find((vv) => v.col_type == vv)) {
                return v
            } else if (v.url.includes('@rule=')) {
                let [_, url, rule] = v.url.match(/^([\s\S]*?)@rule=([\s\S]*)$/)
                v.url = url
                MY__RULE.detail_find_rule = rule
                MY__RULE.detail_col_type = MY_RULE.col_type
            } else if (v.url.startsWith('hiker://page/')) {
                if (v.url.includes('rule=') || (v.extra || {}).rule)
                    return v
                let [_, path, flag, params] = v.url.match(/^hiker:\/\/page\/(.+?)(#.*?)?(?:\?(.*))?$/),
                    subPage = MY_RULE.pageList.find((v) => v.path == path),
                    subUrl = (params || '').split('&').find((v) => v.startsWith('url='))
                v.url = (subUrl ? subUrl.slice(4).replace(/ï¼Ÿï¼Ÿ/g, '?').replace(/ï¼†ï¼†/g, '&') : (v.extra || {}).url) || 'hiker://empty' + flag + '?' + (params || '')
                MY__RULE.detail_find_rule = subPage.rule
                MY__RULE.detail_col_type = subPage.col_type
            } else if (v.url.includes('@lazyRule=')) {
                let reIndex = v.url.indexOf('.js:')
                if (reIndex >= 0) v.url = v.url.slice(0, reIndex) + '.js:\nif(MY_RULE)Object.assign(MY_RULE, ' + assignLazyRule + ');' + v.url.slice(reIndex + 4)
                return v
            } else if (isIndex) {
                if (!MY_RULE.detail_find_rule || /hiker:\/\/(?!empty.+$)/.test(v.url)) return v
            } else {
                return v
            }
            v.extra = {
                url: genExUrl(v.url),
                RULE: MY__RULE,
                pageTitle: isIndex && v.title
            }
            v.url = 'hiker://page/frameLoad?rule=' + QING_TITLE
            return v
        }))
        method_setResult.invoke(javaContext, myCollection_el, param1, param2, param3)
    }
    setHomeResult = setResult
    setSearchResult = setResult

    eval(MY_RULE.find_rule.slice(3))
} else {
    let [_, findRule, detailFindRule] = MY_RULE.find_rule.match(/^([\s\S]*?)(?:==>([\s\S]*))?$/)
    findRule = findRule.split(';')
    parseDomForArray(getResCode(), findRule.shift()).forEach((data) => {
        let [title, img, desc, url] = findRule.map((v, i) => {
                try {
                    if (v == '*') return ''
                    else v = (i == 1 || i == 3) ?
                        parseDom(data, v) :
                        parseDomForHtml(data, v)
                    if (i != 3) v = runCode(v)
                    return v
                } catch (e) { return '' }
            }),
            res = {
                title: title, url: url,
                desc: desc, img: img,
                col_type: MY_RULE.col_type
            }
        if (res.url) {
            if (res.url.includes('@lazyRule=')) {
                let reIndex = res.url.indexOf('.js:')
                if (reIndex >= 0) res.url = res.url.slice(0, reIndex) + '.js:\nif(MY_RULE)Object.assign(MY_RULE, ' + assignLazyRule + ');' + res.url.slice(reIndex + 4)
            } else  if (detailFindRule) {
                res.extra = {
                    url: genExUrl(url),
                    RULE: Object.assign(genMyRule(), { detail_find_rule: detailFindRule }),
                    pageTitle: isIndex && title
                }
                res.url = 'hiker://page/frameLoad?rule=' + QING_TITLE
            } else if (MY_RULE.detail_find_rule) {
                res.extra = {
                    url: genExUrl(url),
                    RULE: genMyRule(),
                    pageTitle: isIndex && title
                }
                res.url = 'hiker://page/frameLoad?rule=' + QING_TITLE
            }
        }
        myCollection_el.push(res)
    })
    setResult(myCollection_el)
}

$.exports = function(showAll) {
    let url = getMyVar('remoteUrl'),
        data = []
    if (url.startsWith('hiker://') || url.startsWith('file://')) {
        // å¦‚æœä¸ºæœ¬åœ°æ–‡ä»¶åˆ™ä¸è¿›è¡Œç¼“å­˜
        try {
            let oriData = fetch(url)
            if (url.startsWith('hiker://page/'))
                oriData = JSON.parse(oriData).rule
            data = JSON.parse(oriData)
        } catch (e) {}
    } else {
        // è·å–è¿œç¨‹æ•°æ®ï¼Œæ£€æŸ¥é—´éš”é»˜è®¤ä¸€å¤©
        let interval = parseInt(readFile('updateInterval') || '1'),
            oldData = fetch('hiker://files/libs/' + md5(url) + '.js'),
            remoteData = []
        if(interval == 0) interval = -1
        try { remoteData = JSON.parse(fetchCache(url, 24 * interval, {timeout:50000})) } catch(e) {}
        if(!Array.isArray(remoteData)) remoteData = []
        if (remoteData.length < 3) {
            log('æœªè·å–åˆ°æ•°æ®')
            if (oldData) writeFile('hiker://files/libs/' + md5(url) + '.js', oldData)
            data = JSON.parse(oldData || '[]')
        } else {
            log('å·²è·å–åˆ°æ•°æ®')
            data = remoteData
        }
    }
    data = data.filter((v) => { return v.title != getMyVar('myCollection') && !(v.author || '').includes('è½»åˆé›†ç”Ÿæˆå™¨') })
    if (readFile('disableCustom')) return data
    else { // å†™å…¥è‡ªå®šä¹‰æ•°æ®
        let customData = JSON.parse(readFile('customData') || '[]'),
            rewriteData = []
        customData = customData.reduce((self, v) => {
            let index = data.findIndex((vv) => v.title == vv.title)
            if (index >= 0) {
                self.push(v)
                let rule = data.splice(index, 1)[0]
                if (showAll || v.visible)
                    rewriteData.push(Object.assign(rule, v))
            }
            return self
        }, [])
        data.forEach((v) => customData.push({ title: v.title, visible: true }))
        saveFile('customData', JSON.stringify(customData))
        return rewriteData.concat(data)
    }
}

