functionÂ formatDate2(inputTime)Â {
Â Â Â Â varÂ dateÂ =Â newÂ Date(inputTime);
Â Â Â Â varÂ yÂ =Â date.getFullYear();
Â Â Â Â varÂ mÂ =Â date.getMonth()Â +Â 1;
Â Â Â Â mÂ =Â mÂ <Â 10Â ?Â ('0'Â +Â m)Â :Â m;
Â Â Â Â varÂ dÂ =Â date.getDate();
Â Â Â Â dÂ =Â dÂ <Â 10Â ?Â ('0'Â +Â d)Â :Â d;
Â Â Â Â returnÂ y +'-' + mÂ +Â '-'Â +Â d;
};

functionÂ formatDate(now)Â {
Â Â Â Â varÂ timeÂ =Â formatDate2(now)
Â Â Â Â varÂ date1Â =Â newÂ Date(now)
Â Â Â Â varÂ date2Â =Â newÂ Date()
Â Â Â Â varÂ date3Â =Â date2.getTime()Â -Â date1.getTime()
Â Â Â Â varÂ daysÂ =Â Math.floor(date3Â /Â (24Â *Â 3600Â *Â 1000))
Â Â Â Â varÂ leave1Â =Â date3Â %Â (24Â *Â 3600Â *Â 1000)Â 
Â Â Â Â varÂ hoursÂ =Â Math.floor(leave1Â /Â (3600Â *Â 1000))
Â Â Â Â varÂ leave2Â =Â leave1Â %Â (3600Â *Â 1000)
Â Â Â Â varÂ minutesÂ =Â Math.floor(leave2Â /Â (60Â *Â 1000))
Â Â Â Â varÂ leave3Â =Â leave2Â %Â (60Â *Â 1000)
Â Â Â Â ifÂ (daysÂ <Â 1Â &&Â hoursÂ <Â 1)Â {Â returnÂ minutesÂ +Â "åˆ†å‰"Â }Â elseÂ ifÂ (daysÂ <Â 1)Â {Â returnÂ hoursÂ +Â "å°æ—¶Â "Â +Â minutesÂ +Â "åˆ†å‰"Â }Â elseÂ {
Â Â Â Â Â Â Â Â ifÂ (daysÂ <Â 7)Â {
Â Â Â Â Â Â Â Â Â Â Â Â returnÂ daysÂ +Â "å¤©Â "Â +Â hoursÂ +Â "å°æ—¶å‰Â "
Â Â Â Â Â Â Â Â }Â elseÂ {Â returnÂ timeÂ }
Â Â Â Â }
}

