function getList(url, d, col) {
    html = fetch(url);
    Json = JSON.parse(html || "[]");
    J = Json.data.cards
    J.forEach((x) => {
        if (x.mblog) {
            mblog = x.mblog;
            d = pu(d, mblog, col)
        }
        if (x.card_group) {
            x.card_group.forEach((j) => {
                mblog = j.mblog
                if (mblog) {
                    d = pu(d, mblog, col)
                }
                if (j.left_element) {
                    mblog = j.left_element.mblog;
                    if (mblog) {
                        d = pu(d, mblog, col)
                    } else {
                        d = pu(d, j.right_element.mblog, col)
                    }
                }
            })
        }
    })
    return d
}
