$.exports.isLoggedIn = function () {
    if (fileExist("hiker://files/rules/cy/bili.txt")) {
        biliData = JSON.parse(request("hiker://files/rules/cy/bili.txt"));
        if (biliData.userList.length == 0) {
            return JSON.stringify({
                "userName": 'æœªç™»å½•',
                "userMid": '',
                "userFace": '',
                "type": false
            })
        }
        currentUser = biliData.userList[0];
        var cookie = currentUser.cookie;
        let userinfo = JSON.parse(fetch("https://api.bilibili.com/x/web-interface/nav", {
            headers: {
                "Cookie": cookie
            }
        })).data;
        if (userinfo.isLogin === false) {
            return JSON.stringify({
                "userName": 'æœªç™»å½•',
                "userMid": '',
                "userFace": '',
                "type": false
            })
        } else {
            currentUser.userName = userinfo.uname;
            currentUser.userMid = userinfo.mid;
            // 1ï¼šä¼šå‘˜å¼€é€šï¼›0ï¼šæœªå¼€é€š
            currentUser.vipStatus = userinfo.vipStatus;
            currentUser.face = userinfo.face + "@Referer=";
            saveFile('hiker://files/rules/cy/bili.txt', JSON.stringify(biliData));
            // return true;
            return JSON.stringify({
                "userName": currentUser.userName,
                "userMid": currentUser.userMid,
                "userFace": currentUser.face,
                "type": true
            });
        }
    } else {
        return JSON.stringify({
            "userName": 'æœªç™»å½•',
            "userMid": '',
            "userFace": '',
            "type": false
        });
    }
}
