$.exports.biliLazy = function (id, cid, qn) {
    // é‡ç½®åŸç”Ÿå¼¹å¹•ç»Ÿä¸€å¼¹å¹•å¤§å°ã€ä¿®æ”¹æ­¥é•¿
    function overrideSave(dm) {
        var danmuData = request(dm);
        // å­—ä½“å¤§å°ã€å¼¹å¹•æ­¥é•¿
        var danmuSetting = JSON.parse(readFile('hiker://files/dmç›’å­/settings.json'));
        var size = danmuSetting.fontSize;
        var step = danmuSetting.step;
        var data = '';
        td = pdfa(danmuData, 'i&&d');
        for (let i = 0; i < td.length; i += step) {
            timepoint = pdfh(td[i], 'd&&p').split(",")[0];
            ct = pdfh(td[i], 'd&&p').split(",")[1];
            color = pdfh(td[i], 'd&&p').split(",")[3];
            // å¼¹å¹•å†…å®¹
            content = pdfh(td[i], 'd&&Text');
            if (content.indexOf('<') != -1 || content.indexOf('>') != -1 || content.indexOf('&') != -1 || content.indexOf('\u0000') != -1 || content.indexOf('\b') != -1) {
                continue;
            }
            tempdata = `<d p="${Math.round(timepoint)},${ct},${size},${color},0">${content}</d>\n`;
            data += tempdata;
        }
        danmustr = `<?xml version="1.0" encoding="UTF-8"?>\n<i>\n${data}</i>`;
        saveFile(dm, danmustr);
    }
    // åˆ¤æ–­æ˜¯å¦é‡å†™
    var dm = 'https://comment.bilibili.com/' + cid + '.xml';
    if (getItem('isOverrideSave', 'false') == 'true') {
        file = 'https://comment.bilibili.com/' + cid + '.xml';
        dm = 'hiker://files/cache/danmu/' + 'bili_from_' + getPageTitle() + '.xml';
        downloadFile(file, dm);
        overrideSave(dm);
    }
    var data = $.require("hiker://page/api").getUrl(id, cid, qn);
    // æäº¤å†å²è®°å½•
    $.require("hiker://page/api").reportProcess(id, cid);
    // qualityNum=dash.audio.length;ä¸éœ€è¦äº†ï¼Œæ‰¾åˆ°äº†æ›´å¥½çš„åˆ¤æ–­æ–¹æ³•ï¼Œè™½ç„¶æˆ‘å¹¶ä¸çŸ¥é“è¿™äº›å‚æ•°çš„å«ä¹‰æ˜¯ä»€ä¹ˆ
    videoList = [];
    audioList = [];
    nameList = [];
    headers = [];
    try {
        data.audio.forEach(item => {
            audioList.push(item.baseUrl);
            headers.push({
                "Referer": "https://www.bilibili.com",
                "User-Agent": PC_UA
            });
        });
        data.video.forEach(item => {
            if (item.codecid == 7) {
                videoList.push(item.baseUrl + "#isVideo=true#");
                if (item.id == 126) {
                    nameList.push('æœæ¯”è§†ç•Œ');
                } else if (item.id == 125) {
                    nameList.push('HDR çœŸå½©è‰²');
                } else if (item.id == 120) {
                    nameList.push('4Kè¶…æ¸…');
                } else if (item.id == 116) {
                    nameList.push('1080P60é«˜å¸§ç‡');
                } else if (item.id == 112) {
                    nameList.push('1080P+é«˜ç ç‡');
                } else if (item.id == 80) {
                    nameList.push('1080Pé«˜æ¸…');
                } else if (item.id == 74) {
                    nameList.push('720P60é«˜å¸§ç‡');
                } else if (item.id == 64) {
                    nameList.push('720Pé«˜æ¸…');
                } else if (item.id == 32) {
                    nameList.push('480Pæ¸…æ™°');
                } else if (item.id == 16) {
                    nameList.push('360Pæµç•…');
                } else if (item.id == 6) {
                    nameList.push('240Pæé€Ÿ');
                }
            }
        });
        // ä¿æŒé•¿åº¦ä¸€è‡´
        while (audioList.length < videoList.length) {
            // å¤åˆ¶ audioList ä¸­æœ€åä¸€ä¸ªå…ƒç´ å¹¶è¿½åŠ åˆ°æ•°ç»„æœ«å°¾
            audioList.push(audioList[audioList.length - 1]);
            // å¤åˆ¶ headers ä¸­æœ€åä¸€ä¸ªå…ƒç´ å¹¶è¿½åŠ åˆ°æ•°ç»„æœ«å°¾
            headers.push(headers[headers.length - 1]);
        }
        return JSON.stringify({
            urls: videoList,
            audioUrls: audioList,
            headers: headers,
            names: nameList,
            danmu: dm
        })
    } catch (e) {
        log("æ­£å¸¸è§£é‡Šå¤±è´¥ï¼Œè¿›è¡Œæœ€åå°è¯•:");
        return JSON.stringify({
            urls: [data.durl[0].url + "#isVideo=true#"],
            names: [data.quality + ""],
            danmu: dm,
            headers: [{
                "Referer": "https://www.bilibili.com",
                "User-Agent": PC_UA
            }]
        })
    }
}
