$.exports.getSearchRes = function (search_type, seaword, page, pageSize) {
    queryJson = {
        __refresh__: true,
        _extra: '',
        context: '',
        page: page,
        page_size: pageSize,
        order: '',
        duration: '',
        from_source: '',
        from_spmid: 333.337,
        platform: 'pc',
        highlight: 1,
        single_column: 0,
        keyword: seaword,
        search_type: search_type,
        preload: true,
        com2co: true
    }
    parms = $.require("hiker://page/api").getWbiEnc(queryJson);
    dataLink = 'https://api.bilibili.com/x/web-interface/wbi/search/type?' + parms;
    cookie = JSON.parse(request("hiker://files/rules/cy/bili.txt")).userList[0].cookie;
    res = JSON.parse(fetch(dataLink, {
        headers: {
            "User-Agent": PC_UA,
            "Referer": "https://www.bilibili.com/",
            "Cookie": cookie
        },
        method: "GET"
    })).data;
    return res;
}
