$.exports.timeConvert = function (time) {
    function formatDate(e, t) {
        e = new Date(e * 1000); //æ—¶é—´æˆ³ä¸º10ä½éœ€*1000ï¼Œæ—¶é—´æˆ³ä¸º13ä½çš„è¯ä¸éœ€ä¹˜1000
        return t.replace(/yyyy|YYYY/, e.getFullYear())
            .replace(/yy|YY/, r(e.getFullYear() % 100, 2))
            .replace(/mm|MM/, r(e.getMonth() + 1, 2))
            .replace(/m|M/g, e.getMonth() + 1)
            .replace(/dd|DD/, r(e.getDate(), 2))
            .replace(/d|D/g, e.getDate())
            .replace(/hh|HH/, r(e.getHours(), 2))
            .replace(/h|H/g, e.getHours())
            .replace(/ii|II/, r(e.getMinutes(), 2))
            .replace(/i|I/g, e.getMinutes())
            .replace(/ss|SS/, r(e.getSeconds(), 2))
            .replace(/s|S/g, e.getSeconds())
            .replace(/w/g, e.getDay())
            .replace(/W/g, ["æ—¥", "ä¸€", "äºŒ", "ä¸‰", "å››", "äº”", "å…­"][e.getDay()])
    }

    function r(e, t) {
        for (var r = 0, n = t - (e + "").length; r < n; r++) e = "0" + e;
        return e + ""
    }
    return formatDate(time, 'YYYY-MM-DD hh:ii:ss');
    // console.log(formatDate(1664089532, 'YYYY-MM-DD hh:ii:ss'));
}
// ç§’æ•°åˆ†é’Ÿè¡¨è¾¾è½¬æ¢å‡½æ•°
$.exports.formatSeconds = function (seconds) {
    let minutes = Math.floor(seconds / 60); // è®¡ç®—åˆ†é’Ÿæ•°
    let remainingSeconds = seconds % 60; // è®¡ç®—å‰©ä½™çš„ç§’æ•°
    let formattedTime = `${minutes}:${String(remainingSeconds).padStart(2, '0')}`;
    return formattedTime;
}

// Bç«™Wbiç­¾åç®—æ³•(After 2023-3,get request with wbi.)
$.exports.getWbiEnc = function (queryJson) {
    const mixinKeyEncTab = [
        46, 47, 18, 2, 53, 8, 23, 32, 15, 50, 10, 31, 58, 3, 45, 35, 27, 43, 5, 49,
        33, 9, 42, 19, 29, 28, 14, 39, 12, 38, 41, 13, 37, 48, 7, 16, 24, 55, 40,
        61, 26, 17, 0, 1, 60, 51, 30, 4, 22, 25, 54, 21, 56, 59, 6, 63, 57, 62, 11,
        36, 20, 34, 44, 52
    ];

    // å¯¹ imgKey å’Œ subKey è¿›è¡Œå­—ç¬¦é¡ºåºæ‰“ä¹±ç¼–ç 
    function getMixinKey(orig) {
        let temp = '';
        mixinKeyEncTab.forEach((n) => {
            temp += orig[n]
        });
        return temp.slice(0, 32);
    }

    // ä¸ºè¯·æ±‚å‚æ•°è¿›è¡Œ wbi ç­¾å
    function encWbi(params, img_key, sub_key) {
        const mixin_key = getMixinKey(img_key + sub_key),
            curr_time = Math.round(Date.now() / 1000),
            chr_filter = /[!'\(\)*]/g
        let query = [];
        params = Object.assign(params, { wts: curr_time });    // æ·»åŠ  wts å­—æ®µ
        // æŒ‰ç…§ key é‡æ’å‚æ•°
        Object.keys(params).sort().forEach((key) => {
            query.push(
                encodeURIComponent(key) +
                '=' +
                // è¿‡æ»¤ value ä¸­çš„ "!'()*" å­—ç¬¦
                encodeURIComponent(('' + params[key]).replace(chr_filter, ''))
            )
        })
        query = query.join('&');
        const wbi_sign = md5(query + mixin_key); // è®¡ç®— w_rid
        return query + '&w_rid=' + wbi_sign;
    }

    // è·å–æœ€æ–°çš„ img_key å’Œ sub_key
    function getWbiKeys() {
        const json_content = JSON.parse(fetch('https://api.bilibili.com/x/web-interface/nav', {
            headers: {
                "User-Agent": PC_UA,
                "Referer": "https://www.bilibili.com/"
            },
            method: 'GET'
        })).data;
        img_url = json_content.wbi_img.img_url;
        sub_url = json_content.wbi_img.sub_url;
        return {
            img_key: img_url.substring(img_url.lastIndexOf('/') + 1, img_url.length).split('.')[0],
            sub_key: sub_url.substring(sub_url.lastIndexOf('/') + 1, sub_url.length).split('.')[0]
        }
    }

    const wbi_keys = getWbiKeys();

    const query = encWbi(
        queryJson,
        wbi_keys.img_key,
        wbi_keys.sub_key
    );
    return query;
}

