const cookie = getItem("quarkCookie", "");
let httpRequest = $.require("httpRequest");

function CookieManage(cookie) {
    this.cookie = {};
    this.add(cookie);
}
Object.assign(CookieManage.prototype, {
    add(cookie) {
        let cookies = [];
        if (typeof cookie === "string") {
            cookies = cookie.split(";");
        } else if (Array.isArray(cookie)) {
            cookies = cookie;
        }
        cookies.forEach(v => {
            v = v.split("=");
            if (v.length < 2) return;
            this.cookie[v[0].trim()] = v[1].trim();
        });
    },
    get() {
        return Object.entries(this.cookie).map(v => v[0] + "=" + v[1]).join(";");
    }
});
const cookieManage = new CookieManage(cookie);

function waitSleep(time) {
    java.lang.Thread.sleep(time || 600);
}

function requestHttp(url) {
    return new httpRequest(url)
        .success((body, res) => {
            if (body.status !== 200) {
                throw new Error(body.message);
            }
            cookieManage.add(res.headers["set-cookie"]);
            return body;
        })
        .errorType((err, res) => {
            return new Error(JSON.parse(res.body).message);
        })
        .bodyToString(true)
        .headers({
            cookie: cookieManage.get(),
            "User-Agent": "Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) quark-cloud-drive/2.5.20 Chrome/100.0.4896.160 Electron/18.3.5.4-b478491100 Safari/537.36 Channel/pckk_other_ch",
            "referer": "https://drive.quark.cn/"
        });
}

function getDriveInfo() {
    return requestHttp("https://drive.quark.cn/1/clouddrive/share/sharepage/dir?pr=ucpro&fr=pc&aver=1").start(true);
}

function createSaveTask(fid, fid_token, pwd_id, stoken) {
    let driveInfo = getDriveInfo();
    return requestHttp("https://drive.quark.cn/1/clouddrive/share/sharepage/save?pr=ucpro&fr=pc").setStartRequestFunc(post).body({
        "fid_list": [
            fid
        ],
        "fid_token_list": [
            fid_token
        ],
        "to_pdir_fid": driveInfo.data.dir.fid,
        "pwd_id": pwd_id,
        "stoken": stoken,
        "pdir_fid": driveInfo.data.dir.pdir_fid
    }).start(true);
}

function createDeleteTask(fid) {
    let driveInfo = getDriveInfo();
    return requestHttp("https://drive.quark.cn/1/clouddrive/file/delete?pr=ucpro&fr=pc").setStartRequestFunc(post).body({
        "action_type": 2,
        "filelist": [
            fid
        ],
        "exclude_fids": []
    }).start(true);
}

function task(task_id, retry_index) {
    return requestHttp("https://drive.quark.cn/1/clouddrive/task").buildUrl({
        pr: "ucpro",
        fr: "pc",
        task_id: task_id,
        retry_index: retry_index
    }).start(true);
}

function queryTask(task_id) {
    for (let i = 0; i < 6; i++) {
        try {
            let res = task(task_id, i + "");
            if (res.data.status === 2) {
                waitSleep(1000);
                return res.data.save_as.save_as_top_fids[0];
            }
            waitSleep(300);
        } catch (e) {}
    }
    return void(0);
}

function queryTaskStatus(task_id) {
    for (let i = 0; i < 6; i++) {
        try {
            let res = task(task_id, i + "");
            if (res.data.status === 2) {
                waitSleep(1000);
                return true;
            }
            waitSleep(300);
        } catch (e) {}
    }
    return false;
}

function getPreviewVideo(fid) {
    return requestHttp("https://drive.quark.cn/1/clouddrive/file/v2/play?pr=ucpro&fr=pc").body({
        "fid": fid,
        "resolutions": "normal,low,high,super,2k,4k",
        "supports": "fmp4,m3u8"
    }).setStartRequestFunc(post).start(true);
}

function getDownloadUrl(fid) {
    return requestHttp("https://drive.quark.cn/1/clouddrive/file/download?pr=ucpro&fr=pc").body({
        "fids": [fid]
    }).setStartRequestFunc(post).start(true);
}

function getPlayUrl(fid, fid_token, pwd_id, stoken, category) {
    try {
        if (!cookie) return "toast://è¯·ç™»å½•è´¦å·";
        let taskId = createSaveTask(fid, fid_token, pwd_id, stoken).data.task_id;
