                    headers: [playHeader]
                }
                createDeleteTask(asfid);
                return JSON.stringify(playList);
            }
        } else { // ä¸æ˜¯è§†é¢‘èµ„æº
            let playInfo = getDownloadUrl(asfid).data[0];
            let playUrl = playInfo.download_url;

            // éŸ³é¢‘èµ„æº
            if (playInfo.format_type.includes('audio') || playInfo.file_name.match(/\.(aac|ac3|aiff|amr|m4a|mp2|mp3|ogg|ra|au|wav|wma|mka|flac|wav)/)) {
                let playList = {
