} else {
    let { runCode } = $.require('hiker://page/funcTools?rule=' + QING_TITLE),
        [_, findRule, detailFindRule] = MY_RULE.find_rule.match(/^([\s\S]*?)(?:==>([\s\S]*))?$/)
    findRule = findRule.split(';')
    parseDomForArray(getResCode(), findRule.shift()).forEach((data) => {
