                c[i] = che;
                log("clear:" + url)
                saveFile("chapter.json", JSON.stringify(c))
                break
            }
        }
    }, url))
    for (let it of caches) {
        if (it.url == url) {
            return it;
        }
    }
    return {
        url: url,
        next: "",
        chapters: []
    }
}

function saveCache(che) {
    let c = caches
    if (c.length > 10) {
        c.shift()
    }
    let exist = false
    for (let i = 0; i < c.length; i++) {
        if (c[i].url == che.url) {
            c[i] = che;
            exist = true;
            break
        }
    }
    if (!exist) {
        c.push(che)
    }
    saveFile("chapter.json", JSON.stringify(c))
}


