var convert = (str) => {
    return str.replace(/@/g, "&&")
        .replace(/\.[0-9]/g, function(a) {
            return a.replace(".", ",")
        })
        .replace(new RegExp(" ", "g"), "&&")
        .replace(/text\.(.*?)&&/g, "*:contains($1),-1&&")
        .replace(/-class\./g, ".")
        .replace(/class\./g, ".")
        .replace(/tag\./g, "")
        .replace(/id\./g, "#")
        .replace(/##/g, "!")
        .replace(/&&([a-zA-Z\.\-]+?)!([0-9])/g, "--$1,$2&&$1")
        .replace("text", "Text");
};
let getArray = (html, rule) => {
    let r1 = rule.split("!")[0].split("||");
    let c = convert(r1[0]);
    if (c.includes("&&li&&a") && c[c.length - 1] == "a") {
        c = c.replace("&&li&&a", "&&li");
    }
    log(c);
    let arr = pdfa(html, "body&&" + c);
    if (r1.length > 1) {
        let r2 = r1[1].split(":");
        let r3 = [];
        for (let it of r2) {
            try {
                let i = parseInt(it);
                if (isNaN(i)) {
                    continue;
                }
                r3.push(i);
            } catch (e) {}
        }
        let arr2 = [];
        for (let i = 0; i < arr.length; i++) {
            if (r3.indexOf(i) < 0) {
                arr2.push(arr[i]);
            }
        }
        return arr2;
    }
    log("è·å–å¤±è´¥ï¼Œå¤±è´¥å®šä½ï¼š" + r1)
    return arr;
};
let getText = (html, rule) => {
    var ru = convert(rule)
    var r = pdfh(html, ru)
    if (r == "") {
        log("è¿”å›ä¸ºç©ºï¼Œå¯èƒ½æ˜¯å®šä½å¤±è´¥ï¼š" + rule)
    }
    return r
};
let getUrl0 = (html, parentRule, rule) => {
    if (parentRule != null) {
        parentRule = parentRule.split("!")[0];
    }
    let p = parentRule != null && parentRule[parentRule.length - 1] == "a" ? "a&&" : "";
    let r = pd(html, p + convert(rule.split("##")[0]));
    if (r == "") {
        log("è¿”å›ä¸ºç©ºï¼Œå¯èƒ½æ˜¯å®šä½å¤±è´¥ï¼š" + rule)
    }
    return r
};
let parseUrl = (url, page) => {
    url = url.replace(new RegExp("{{page}}", "g"), page);
    let s = url.split("<,");
    if (s.length > 1) {
        if (page > 1) {
            let s2 = s[1].split(">");
            url = s[0] + s2[0] + (s2.length > 1 ? s2[1] : "")
        } else {
            url = s[0];
        }
    }
    return url;
}
let getCharset = (searchUrl) => {
    if (searchUrl) {
        if (searchUrl.includes('"gbk"') || searchUrl.includes("'gbk'")) {
            return "gbk"
        } else if (searchUrl.includes('"gb2312"') || searchUrl.includes("'gb2312'")) {
            return "gb2312"
        }
    }
    return null
}
$.exports = {
