var {
    convert,
    parseUrl,
    getCharset
} = $.require("hiker://page/converter");
let search = (rule, kw, page) => {
    let d = [];
    let m = rule.ruleSearch
    // é˜…è¯»=>è§†ç•Œ
    let u = rule.searchUrl.startsWith("http") ? rule.searchUrl : rule.bookSourceUrl + rule.searchUrl;
    //log(m);
    let url1 = parseUrl(u, page).replace(new RegExp("{{key}}", "g"), kw);
    let url2 = url1.split(",{");
    let url = url2[0];
    let headers = {};
    let method = "get";
    let body = null;
    let c = getCharset(url1);
    if (c) {
        headers.charset = c;
        headers["Content-Type"] = "text/html; charset=" + c;
    }
    try {
        if (url2.length > 1 && url1.substring(url1.length - 1, url1.length) == "}") {
            let ext = "{" + url1.substring(url1.indexOf(",{") + 2, url1.length);
            //log(ext);
            try {
                ext = JSON.parse(ext);
            } catch (e) {
                eval("ext=" + ext);
            }
            if (ext.method && ext.method != "") {
                method = ext.method;
            }
            if (ext.body && ext.body != "") {
                body = ext.body;
                headers["Content-Type"] = "application/x-www-form-urlencoded" + (headers.charset ? ";charset=" + headers.charset : "");
            }
        }
    } catch (e) {
        if (url1.includes('"POST"') || url1.includes('"post"')) {
            method = "post";
        }
    }
    if (method == "post" || method == "POST") {
        let ct = headers["Content-Type"];
        headers["Content-Type"] = ct ? ct.replace("text/html", "application/x-www-form-urlencoded") : "application/x-www-form-urlencoded";
    }

    let listrule = "body&&" + convert(m.bookList).split("||")[0]
    let author = null;
    try {
        author = convert(m.author);
    } catch (e) {}
    let cover = null;
    try {
        cover = convert(m.coverUrl);
    } catch (e) {}
    let name = null;
    try {
        name = convert(m.name);
    } catch (e) {}
    let bookurl = null;
    try {
        bookurl = convert(m.bookUrl);
    } catch (e) {}
    let intro = null;
    try {
        intro = convert(m.intro);
    } catch (e) {}
    //setPageTitle("æœç´¢" + getParam("kw") + " " + MY_PARAMS.bookSourceName);
    //MY_URL = url;
    let res = request(url, {
        method: method,
        headers: headers,
        body: body
    });
    let pdfh1 = (a, b) => {
        try {
            if (b == null) return "";
            return pdfh(a, b);
        } catch (e) {
            return "";
        }
    }
    let pd1 = (a, b, c) => {
        try {
            if (b == null) return "";
            return pd(a, b, c);
        } catch (e) {
            return "";
        }
    }
    listrule = listrule.replace("&&tbody--tr,0&&tr", "&&table tbody tr:gt(0)");
    //log(listrule);
    let list = pdfa(res, listrule);
    //log(list);
    //log(name);
    list.forEach(item => {
        let next = rule.ruleToc.nextTocUrl;
        let nexttoc = next && next != "";
        let ac = nexttoc ? "": "#autoCache#";
        //let ac = "#autoCache#";
        let u1 = pd1(item, bookurl, url) + ac + "#immersiveTheme#";
        if (c) {
            u1 = u1 + ";get;" + c
        }
        d.push({
            title: pdfh1(item, name),
            img: pd1(item, cover, url),
            url: "hiker://page/menu",
            desc: rule.bookSourceName + "\n" + pdfh1(item, author) + "\n" + pdfh1(item, intro),
            extra: {
                "Toc": rule.ruleToc,
                "content": rule.ruleContent,
                "bookName": parseDomForHtml(item, name),
                "cover": parseDom(item, cover, url),
                url: nexttoc ? "hiker://empty#" + u1 : u1,
                u1: nexttoc ? u1 : null,
                inheritTitle: false,
                charset: c
            }
        })
    });
    return d;
}
$.exports = {
    convert: convert,
    search: search,
    listRules: (page, rule) => {
        try {
            //å¹¶å‘æ•°é‡
            let size = 16;
            let {
                load,
                loadRules,
                getSelect
            } = $.require("hiker://page/datat");
            let data = load();
            let select = getSelect(data);
            let rules = data.length > 0 ? loadRules(select.name) : [];
            if (!page && !rule) {
                return rules.map(it => it.name);
            }
            if (rule != null) {
                return rules.filter(i => i.name == rule);
            }
            let start = (page - 1) * size;
            if (rules.length < start + 1) {
                return [];
            }
            let len = size;
            if (rules.length < page * size) {
                len = rules.length - start
            }
            let all = rules.length;
            let use = (page - 1) * size + len;
            return {
                all: all,
                use: use,
                data: rules.slice(start, start + len)
            };
        } catch (e) {
            return {
                all: 0,
                use: 0,
                data: []
            }
        }
    }
}

