    refreshPage();
    return "toast://å·²å¯¼å…¥" + count + "æ¡è®¢é˜…æº";
}

function renderLiveData(url,ua) {
    let m3u = "";
    if (url.startsWith('hiker://') || url.startsWith('file://')) {
        m3u = fetch(url);
    } else {
        let interval = Number(getItem('interval', '24')),
            oldData = fetch('hiker://files/libs/' + md5(url) + '.js'),
            remoteData = '';
        try {
            remoteData = fetchCache(url, interval, {
                headers:{
                    'User-Agent' : ua || MOBILE_UA
                },
                timeout: 50000
            });
        } catch (e) {}
        if (!remoteData.includes('http') || remoteData.includes('<body>')) { 
            log('æ›´æ–°å¤±è´¥');
            if (oldData) writeFile('hiker://files/libs/' + md5(url) + '.js', oldData);
            m3u = oldData;
        } else {
            m3u = remoteData;
        }
    }

    let mode =  m3u.indexOf('#EXTM3U') == 0 , re;
    if(mode){
        re = /(.*),(.*)\s*?((?:http|rtmp)[^#\s]*)/g;
    }else{
        re = /()(?:\B\$c_start|\B#{1,2})?(.*?),?(##|å¤´#|\$c_end|,?#genre#|(?:http|rtmp)[^\s]*)/g;
    }

    let items = {},
        group = 'æœªåˆ†ç»„';
    for (let i = re.exec(m3u); i != null; i = re.exec(m3u)) {
        if (mode) {
            group = (i[1].match(/group-title="(.*?)"/) || [null, group])[1];
        } else if (['#genre#','##','å¤´#','$c_end'].includes(i[3])) {
            group = i[2];
            continue;
        }
        let title = i[2],
            url = i[3].includes('#') ? i[3].split('#') : [i[3]],
            headers = [];
        if(!url[url.length-1]) url.pop();
        url.forEach((v,index,array)=>{
            if(/;\{(.*)\}/.test(v)){
                let obj = {},
                     con = v.match(/;\{(.*)\}/)[1],
                     arr = con.split('&&');
                arr.forEach((i)=>{
                    let [key,value] = i.split('@');
                    obj[key] = value.replace(/;/g,'ï¼›ï¼›');
                })
                array[index] = v.match(/^([^;]*);\{/)[1]
                headers.push(obj);
            }else{
                headers.push({'User-Agent' : MOBILE_UA.replace(/;/g,'ï¼›ï¼›')});
            }
            array[index] = array[index].replace(/;/g,'ï¼›ï¼›')+'#isVideo=true#';
        });
        if (items[group]) {
            if (items[group][title]) {
                items[group][title].url = items[group][title].url.concat(url);
                items[group][title].headers = items[group][title].headers.concat(headers);
            } else {
                items[group][title] = {url:url,headers:headers};
            }
        } else {
            items[group] = {};
            items[group][title] = {url:url,headers:headers};
        };
    }
    return items;
}

