        if(!url[url.length-1]) url.pop();
        url.forEach((v,index,array)=>{
            if(/;\{(.*)\}/.test(v)){
                let obj = {},
                     con = v.match(/;\{(.*)\}/)[1],
                     arr = con.split('&&');
                arr.forEach((i)=>{
