function aesDecryptECB(encryptedData, key) {
    // å°†keyè½¬æ¢ä¸ºCryptoJSæ”¯æŒçš„æ ¼å¼
    const keyCrypto = CryptoJS.enc.Utf8.parse(key);
    // å°†åŠ å¯†çš„Base64å­—ç¬¦ä¸²è½¬æ¢ä¸ºCryptoJSæ”¯æŒçš„æ ¼å¼
    const encryptedCrypto = CryptoJS.enc.Base64.parse(encryptedData);
    // ä½¿ç”¨AES/ECB/PKCS5Paddingè¿›è¡Œè§£å¯†
    const decrypted = CryptoJS.AES.decrypt({
        ciphertext: encryptedCrypto
    }, keyCrypto, {
        mode: CryptoJS.mode.ECB,
        padding: CryptoJS.pad.Pkcs7
    });
    // æ£€æŸ¥è§£å¯†æ˜¯å¦æˆåŠŸ
    if (decrypted) {
        // è¿”å›utf8æ ¼å¼çš„è§£å¯†ç»“æœ
        return decrypted.toString(CryptoJS.enc.Utf8);
    } else {
        return "è§£å¯†å¤±è´¥";
    }
}

// åŠ å¯†å‡½æ•°
function aesEncryptECB(decrypteddata, key) {
    // å°†keyè½¬æ¢ä¸ºCryptoJSæ”¯æŒçš„æ ¼å¼
    const keyCrypto = CryptoJS.enc.Utf8.parse(key);
    // å°†æ•°æ®è½¬æ¢ä¸ºCryptoJSæ”¯æŒçš„æ ¼å¼
    const dataCrypto = CryptoJS.enc.Utf8.parse(decrypteddata);
    // ä½¿ç”¨AES/ECB/PKCS5Paddingè¿›è¡ŒåŠ å¯†
    const encrypted = CryptoJS.AES.encrypt(dataCrypto, keyCrypto, {
        mode: CryptoJS.mode.ECB,
        padding: CryptoJS.pad.Pkcs7
    });
    // æ£€æŸ¥è§£å¯†æ˜¯å¦æˆåŠŸ
    if (encrypted) {
    // è¿”å›Base64æ ¼å¼çš„åŠ å¯†ç»“æœ
    return encrypted.toString();
    } else {
        return "è§£å¯†å¤±è´¥";
    }
}

function md5(uuid) {
    return CryptoJS.MD5(uuid).toString();
}

function hmacSHA256(message, secretKey) {
    // ä½¿ç”¨CryptoJSåº“ä¸­çš„HmacSHA256æ–¹æ³•è¿›è¡ŒåŠ å¯†
    const hash = CryptoJS.HmacSHA256(message, secretKey);

    // å°†åŠ å¯†ç»“æœè½¬æ¢ä¸ºHexæ ¼å¼
    return hash.toString(CryptoJS.enc.Hex);
}

