function colorCode(d) {
    var str = Array.from(d.toString().replace('#', ''));
    if (str.length != 6) {
        return false;
    } else {
        for (var k in str) {
            if (!((str[k] >= '0' && str[k] <= '9') || (str[k] >= 'a' && str[k] <= 'f') || (str[k] >= 'A' && str[k] <= 'F'))) {
                return false;
            }
        }
        return true;
    }
}
function color(d, c) {if (colorCode(d) && !colorCode(c)) {
        var tmp = d;
        d = c;
        c = tmp;
    }
    return 'â€˜â€˜â€™â€™<font color=#' + (c || '000000') + '>' + d + '</font>';
}
function small(d, c) {if (colorCode(d) && !colorCode(c)) {
        var tmp = d;
        d = c;
        c = tmp;
    }
    return 'â€˜â€˜â€™â€™<small><font color=#' + (c || '000000') + '>' + d + '</font></small>';
}
function strong(d, c) {
    if (colorCode(d) && !colorCode(c)) {
        var tmp = d;
        d = c;
        c = tmp;
    }
    return 'â€˜â€˜â€™â€™<strong><font color=#' + (c || '000000') + '>' + d + '</font></strong>';
}
function classTop(index, data, host, d, mode, v, c, f) {
    if (!v) {
        v = 0;
    }
    if (!c) {
        c = 'c';
    }
    if (!f) {
        f = 'scroll_button';
    } else {
        f = 'flex_button'
    }
    var c_title = data.title.split('&');
    if (data.id == '') {
        var c_id = c_title;
    } else if (data.id == '@@@') {
        var c_id = data.title.replace(/^.*?&/, '&').split('&');

    } else {
        var c_id = data.id.split('&');
    }
    c_title.forEach((title, index_c, data) => {
        d.push({
            title: index_c == getMyVar(host + c + 'index' + index, (mode || index == v ? '0' : '-1')) ? small(title, 'FF6699') : small(title,'000000'),
            col_type: f,
            url: $('#noLoading#').lazyRule((index, id, index_c, host, mode, title, v, c) => {
                if (mode) {
                    putMyVar(host + c + index, id);

                } else {
                    putMyVar(host + c, id);
                    for (let n = v; n <= 20; n++) {
                        putMyVar(host + c + 'index' + n, '-1');
                    }
                }
                clearMyVar(host + 'page');
                clearMyVar(host + 'url');
                putMyVar(host + c + 'index' + index, index_c);
                refreshPage(false);
                return 'hiker://empty';
            }, index, c_id[index_c], index_c, host, mode, title, v, c),
        });
    });
    d.push({
        col_type: 'blank_block',
    });
    return d;
}

