js:
let items = [];
let pushin = decodeURIComponent(getParam("pushurl"));
let tvip = getItem('hikertvboxset', '');

items.push({
    title: '当前IP：' + tvip,
    url: "input://" + getItem('hikertvboxset', 'http://' + getIP() + ':9978') + "////请输入TVBox端的IP地址，如http://" + getIP() + ":9978.js:setItem('hikertvboxset',input);refreshPage()",
    col_type: 'flex_button'
});
var purl = pushin;

if (pushin.includes('\n') && !pushin.startsWith('{')) {
    purl = pushin.replace("\n", '$');
}
if (pushin.startsWith('http') && pushin.includes(';{')) {
    purl = JSON.stringify({
        "name": "海阔直链推送",
        "url": pushin
    })
}
if (pushin.startsWith('https://www.aliyundrive.com/s/')) {
    let reftoken = JSON.parse(fetch('hiker://files/rules/icy/icy-ali-token.json', {}))[0].refresh_token;
    purl = JSON.stringify({
        "url": pushin,
        "refresh_token": reftoken
    })
}
if (pushin.includes(':52020')) {
    if (pushin.includes('redirectPlayUrl')) {
        purl = JSON.stringify({
            "name": "海阔投屏直链",
            "url": pushin
        })
    } else {
        purl = JSON.stringify({
            "name": "海阔网页投屏",
            "url": pushin
        })
    }
}

//log('推送'+purl);
var state = request(tvip + '/action', {
    headers: {
        'Content-Type': 'application/x-www-form-urlencoded',
        //'X-Requested-With': 'XMLHttpRequest',
        'Referer': tvip
    },
    timeout: 2000,
    body: 'do=push&url=' + purl.replace(/\&/g, '＆＆'),
    method: 'POST'
});
//log(state);
if (state == 'ok') {
    //log(state);
    items.push({
        title: "✅" + state,
        url: "hiker://empty",
        col_type: "text_center_1"
    });
} else {
    //log(state);
    items.push({
        title: "❎" + state,
        url: "hiker://empty",
        col_type: "text_center_1"
    });

}
setResult(items);

const empty = "hiker://empty"

try {
    var categories = pdfa(html, 大类定位).concat(pdfa(html, 拼接分类))
} catch (e) {
    var categories = pdfa(html, 大类定位)
}

let init_cate = []

for (let i = 0; i < 20; i++) {
    init_cate.push("0")
}

const fold = getVar(MY_RULE.group, "0")
const cate_temp_json = getVar(MY_RULE.title, JSON.stringify(init_cate))
const cate_temp = JSON.parse(cate_temp_json)

if (parseInt(page) === 1) {

    d.push({
        title:  fold === '1' ? '““””<b>' + '🙉'.fontcolor("#FF0000") + '</b>' : '““””<b>' + '🙈'.fontcolor("#1aad19") + '</b>',
        url: $().lazyRule((fold) => {
            putVar(MY_RULE.group, fold === '1' ? '0' : '1');
            refreshPage(false);
            return "hiker://empty"
        }, fold),
        col_type: 'scroll_button',
    })

categories.forEach((category, index) => {
        let sub_categories = pdfa(category, 小类定位);
        if (index === 0) {
            sub_categories.forEach((item, key) => {
                let title = pdfh(item, 分类标题)
                d.push({
                    title: key.toString() === cate_temp[index] ? '““””<b><span style="color: ' + 分类颜色 + '">' + title + '</span></b>' : title,
                    url: $(pd(item, 分类链接) + '#noLoading#').lazyRule((params) => {
                        let new_cate = []
                        params.cate_temp.forEach((cate, index) => {
                            new_cate.push(index === 0 ? params.key.toString() : "0")
                        })
                        putVar(MY_RULE.title, JSON.stringify(new_cate))
                        putVar(MY_RULE.url, input)
                        refreshPage(true)
                        return "hiker://empty"
                    }, {
                        cate_temp: cate_temp,
                        key: key,
                        page: page,
                    }),
                    col_type: 'scroll_button',
                })
            })
            d.push({
                col_type: "blank_block"
            });
        } else if (fold === '1') {
            sub_categories.forEach((item, key) => {
                let title = pdfh(item, 分类标题)
                d.push({
                    title: key.toString() === cate_temp[index] ? '““””<b><span style="color: ' + 分类颜色 + '">' + title + '</span></b>' : title,
                    url: $(pd(item, 分类链接) + '#noLoading#').lazyRule((params) => {
                        params.cate_temp[params.index] = params.key.toString()

                        putVar(MY_RULE.title, JSON.stringify(params.cate_temp))
                        putVar(MY_RULE.url, input)
                        refreshPage(true)
                        return "hiker://empty"
                    }, {
                        cate_temp: cate_temp,
                        index: index,
                        key: key,
                        page: page,
                    }),
                    col_type: 'scroll_button',
                })
            })
            d.push({
                col_type: "blank_block"
            });
        }
    })
}

