var d = [];
var getRangeColors = function() {
    return '#' + ('00000' + (Math.random() * 0x1000000 << 0).toString(16)).substr(-6);
}

const 分类颜色 = getRangeColors()
const 大类定位 = 'body&&.navbar-items'
const 拼接分类 = 'body&&.scroll-content'
const 小类定位 = 'body&&a:not(:matches(首页|今日更新|热榜|APP|福利|热门|回家|专题|留言|排行|Netflix|追剧周表))'
const 分类标题 = 'a&&Text'
const 分类链接 = 'a&&href'
MY_URL = MY_URL.replace('hiker://empty##', '')

let true_url = getVar(MY_RULE.url, MY_URL)
const page = MY_PAGE
if (getVar(MY_RULE.url)) {
    true_url = true_url.replace(/vodtypehtml(\/\d+)/, 'vodshow$1-----------.html').replace('---.html', page + '---.html').replace(/(---\d+.html)/, page + '$1')
}

var html = request(true_url)

eval(JSON.parse(request('hiker://page/dt')).rule)

var list = pdfa(html, 'body&&.module-poster-item');
for (var j in list) {
    d.push({
        title: pdfh(list[j], 'img&&alt'),
        desc: pdfh(list[j], '.module-item-note||.module-item-text&&Text'),
        pic_url: pd(list[j], 'img||.lazy&&data-original||data-src') + '@Referer=',
      extra: {              
                mytitle: pdfh(list[j], 'img&&alt')            
            },
        url: pd(list[j], 'a&&href') + "#immersiveTheme#"
    });
}
setResult(d);

