    }
    if (rulejson.search(/\"vodhref\"/) != -1) {
        writeFile("hiker://files/rules/xyq/hikermovie.json", rulejson);
    }
    if (rulejs.search(/lazyRule/) != -1 && rulejson.search(/\"vodhref\"/) != -1) {
        writeFile("hiker://files/rules/xyq/hikerupdate.txt", new Date() + '');
        confirm({
            title: '提示',
            content: '自动更新完成'
        });
    } else {
        confirm({
            title: '提示',
            content: '自动更新失败'
        });
    }
}



//检测是否有写入时间与规则的js插件文件
if (fileExist("hiker://files/rules/xyq/hikerupdate.txt") && fileExist("hiker://files/rules/xyq/hikermovie.js") && fileExist("hiker://files/rules/xyq/hikermovie.json")) {
    //计算时间间隔
    var start = new Date(fetch("hiker://files/rules/xyq/hikerupdate.txt", {}));
    var nowt = new Date();
