    if (pageTitle) setPageTitle(pageTitle)
    setLastChapterRule(oriRULE.last_chapter_rule)
    MY_PARAMS = MY_RULE.params
} else if (MY_PAGE == 1) {
    tabHeader.load(myColl_el)
    if (RULE.search_url) myColl_el.push({
        title: "搜索",
        desc: "这是轻合集的搜索哦~~",
        url: '"hiker://search?s="+input',
        extra: {
            rules: $.toString((QING_TITLE, title) => {
                let rules = $.require('hiker://page/dataLoad?rule=' + QING_TITLE)
                rules = [rules.find((v) => v.title == title)]
                return JSON.stringify(rules)
            }, QING_TITLE, RULE.title),
            defaultValue: getMyVar('searchKey', ''),
            onChange: "putMyVar('searchKey',input)"
        },
        col_type: "input",
    })
}
// 正文解析
var {
    paramHandle,
    runPreRule,
    genExUrl,
    hijackLazyRule
} = $.require('hiker://page/funcTools?rule=' + QING_TITLE)
runPreRule(oriRULE.preRule)
if (MY_RULE.find_rule.startsWith('js:')) {
    let { hijackEl } = $.require('hiker://page/funcTools?rule=' + QING_TITLE)
    setResult = function(param0, param1, param2, param3) {
        param1 = CALLBACK_KEY
        param2 = MY_RULE
        param3 = MY_TYPE
        if (Array.isArray(param0.data)) param0 = param0.data
        Array.prototype.push.apply(myColl_el, param0.map(hijackEl))
        method_setResult.invoke(javaContext, myColl_el, param1, param2, param3)
    }
    setHomeResult = setResult
    setSearchResult = setResult

    eval(MY_RULE.find_rule.slice(3))
} else {
    let { runCode } = $.require('hiker://page/funcTools?rule=' + QING_TITLE),
        [_, findRule, detailFindRule] = MY_RULE.find_rule.match(/^([\s\S]*?)(?:==>([\s\S]*))?$/)
    findRule = findRule.split(';')
    parseDomForArray(getResCode(), findRule.shift()).forEach((data) => {
        let [title, img, desc, url] = findRule.map((v, i) => {
                try {
                    if (v == '*') return ''
                    else v = (i == 1 || i == 3) ?
                        parseDom(data, v) :
                        parseDomForHtml(data, v)
                    if (i != 3) v = runCode(v)
                    return v
                } catch (e) { return '' }
            }),
            res = {
                title: title, url: url,
                desc: desc, img: img,
                col_type: MY_RULE.col_type
            }
        if (res.url) {
            if (res.url.includes('@lazyRule=')) {
                let reIndex = res.url.indexOf('.js:')
                if (reIndex >= 0) res.url = res.url.slice(0, reIndex) + '.js:' + hijackLazyRule + ';' + res.url.slice(reIndex + 4)
            } else  if (detailFindRule) {
                res.extra = {
                    url: genExUrl(url),
                    RULE: base64Encode(JSON.stringify(Object.assign(genMyRule(), { detail_find_rule: detailFindRule }))),
                    pageTitle: isIndex && title
                }
                res.url = 'hiker://page/frameLoad?rule=' + QING_TITLE
            } else if (MY_RULE.detail_find_rule) {
                res.extra = {
                    url: genExUrl(url),
                    RULE: base64Encode(JSON.stringify(genMyRule())),
                    pageTitle: isIndex && title
                }
                res.url = 'hiker://page/frameLoad?rule=' + QING_TITLE
            }
        }
        myColl_el.push(res)
    })
    setResult(myColl_el)
}

let showAll = $.importParam,
    url = getMyVar('remoteUrl'),
    data = []
if (url.startsWith('hiker://') || url.startsWith('file://')) {
    // 如果为本地文件则不进行缓存
    try {
        let oriData = fetch(url)
        if (url.startsWith('hiker://page/'))
            oriData = JSON.parse(oriData).rule
        data = JSON.parse(oriData)
    } catch (e) {}
} else {
    // 获取远程数据，检查间隔默认一天
    let interval = parseInt(readFile('updateInterval') || '1'),
        oldData = fetch('hiker://files/libs/' + md5(url) + '.js'),
        remoteData = []
    if (interval == 0) interval = -1
    try { remoteData = JSON.parse(fetchCache(url, 24 * interval, { timeout: 50000 })) } catch (e) {}
    if (!Array.isArray(remoteData)) remoteData = []
    if (remoteData.length < 3) {
        log('未获取到数据')
        if (oldData) writeFile('hiker://files/libs/' + md5(url) + '.js', oldData)
        data = JSON.parse(oldData || '[]')
    } else {
        log('已获取到数据')
        data = remoteData
    }
