    let pageNum = parseInt(param.split('###')[1]),
        url = MY_RULE.url,
        { fyAll, tabHeader } = $.require('hiker://page/ClassTab?rule=' + QING_TITLE, Object.assign({ QING_TITLE: QING_TITLE }, MY_RULE))

