js:
var lazy = $('').lazyRule(() => {
    if (input.indexOf('magnet:?xt') == -1) {
        try {
            var html = JSON.parse(request(input).match(/r player_.*?=(.*?)</)[1])
            var url = html.url
            if (html.encrypt == '1') {
                url = unescape(url);
            } else if (html.encrypt == '2') {
                url = unescape(base64Decode(url));
            }
            if (/m3u8|mp4/.test(url)) {
                return url
            } else {
                requireCache('hiker://page/globalParse?rule=道长仓库Pro', 24);
                return lazyParse(input);
            }
        } catch (e) {
            log('报错改web嗅探');
            //requireCache('hiker://page/globalParse?rule=道长仓库Pro', 24);
            //return lazyParse(input);
            return "video://" + input
        }
    } else {
        return input
    }
});

