js:
let api = {
  host: "https://www.1bt0.com",
  cookie: {
    get() {
      return getMyVar("cookie", "")
    },
    set(value) {
      putMyVar("cookie", value);
    },
    match(value) {
      let result = value.match(/document.cookie = "(.*?);/g)[0].replace(/(document.cookie = "|;)/g, '');
      //log(result);
      return result;
    }
  },
  getOptions(options) {
    options = options || {};
    let def = {
      headers: {
        Referer: this.host,
        Cookie: this.cookie.get()
      }
    }
    let obj = this.deepAssignNonEmpty({}, def, options);
    //log(obj)
    return obj
  },
  deepAssignNonEmpty(target, ...sources) {
    function isObject(item) {
      return (item && typeof item === 'object' && !Array.isArray(item));
    }
    sources.forEach(source => {
      Object.keys(source).forEach(key => {
        const sourceValue = source[key];
        if (isObject(sourceValue)) {
          if (!target[key]) {
            target[key] = Array.isArray(sourceValue) ? [] : {};
          }
          this.deepAssignNonEmpty(target[key], sourceValue);
        } else if (sourceValue !== null && sourceValue !== undefined && sourceValue !== "") {
          target[key] = sourceValue;
        }
      });
    });
    return target;
  },
  getHtml(url, headers) {
    let options = headers ? headers : {};
    let headers = this.getOptions(options);
    let result = fetch(url, headers);
    var checkCount = 0;
    while (checkCount < 3) {
      if (result.includes('document.cookie')) {
        this.cookie.set(this.cookie.match(result));
        headers = this.getOptions(headers);
        result = fetch(url, headers);
      } else {
        break;
      }
      checkCount++;
    }
    return result;
  },
  getType() {
    let json = {};
    if (fileExist("type.json")) {
      json = JSON.parse(readFile("type.json", 0));
    } else {
      let url = this.host + "/prod/core/system/getVideoTypeList";
      let result = this.getHtml(url);
      try {
        json = JSON.parse(result).data;
        saveFile("type.json", JSON.stringify(json), 0);
      } catch { }
    }
    return json;
  },
  objectToQueryString(obj) {
    return Object.keys(obj).map(key => `${encodeURIComponent(key)}=${encodeURIComponent(obj[key])}`).join('&');
  },
  getList(body,page) {
    let json = {};
    let url = this.host + "/prod/core/system/getVideoMovieList?" + this.objectToQueryString(body) + "&page=" + page;
    //log(url)
    let result = this.getHtml(url);
    try {
      json = JSON.parse(result).data;
    } catch { }
    return json;
  },
  getVlist(body) {
    let json = {};
    let url = this.host + "/prod/core/system/getVideoList?" + this.objectToQueryString(body);
    let result = this.getHtml(url);
    try {
      json = JSON.parse(result).data;
    } catch { }
    return json;
  },
  getTlist(body,page) {
    let json = {};
    let url = this.host + "/prod/core/system/getTList?" + this.objectToQueryString(body) + "&page=" + page;;
    let result = this.getHtml(url);
    try {
      json = JSON.parse(result).data;
    } catch { }
    return json;
  },
  search(keyword, page) {
    let json = {};
    let url = this.host + "/prod/core/system/getVideoList?sb=" + keyword + "&page=" + page;
    let result = this.getHtml(url);
    try {
      json = JSON.parse(result).data;
    } catch { }
    return json;
  },
  getVideo(id) {
    let json = {};
    let url = this.host + "/prod/core/system/getVideoDetail/" + id;
    let result = this.getHtml(url);
    try {
      json = JSON.parse(result).data;
    } catch { }
    return json;
  }
}

let methods = {
  cacheManage: {
    root: "hiker://files/_cache/" + MY_RULE.title + "/",
    getName(id) {
      return this.root + md5(id);
    },
    get(id) {
      let name = this.getName(id);
      if (fileExist(name)) {
        return JSON.parse(fetch(name))
      }
      return {};
    },
    set(id, json) {
      let name = this.getName(id);
      saveFile(name, JSON.stringify(json), 0);
    },
    del(id) {
      let name = this.getName(id);
      deleteFile(name);
    },
    exist(id) {
      let name = this.getName(id);
      if (fileExist(name)) {
        return true;
      } else {
        return false;
      }
    }
  },
  backColor(title, colors) {
    var def = {
      fc: '#FFFFFF',
      bc: '',
    }
    colors = Object.assign(def, colors);
    return '<font color="' + colors.fc + '"><span style="background-color: ' + colors.bc + '">' + title + '</span></font>'
  },
  fontstyle(text, params, col) {
    params = params || undefined;
    var h = params.h !== false ? '““””' : '';
    let ontag = ["icon_small_3"];
    if (col != undefined) {
      if (ontag.includes(col)) {
        h = '';
      }
    }
    var tags = params.tags || "";
    if (params.c != undefined) {
      text = text.fontcolor(params.c);
    }
    if (tags) {
      var tagArray = tags.split('|').filter(e => e);
      var openTags = '';
      var closeTags = '';
      for (var i = 0; i < tagArray.length; i++) {
        openTags += '<' + tagArray[i] + '>';
        closeTags = '</' + tagArray[i] + '>' + closeTags;
      }
      text = openTags + text + closeTags;
    }
    return h + text;
  }
}

$.exports = {
  api: api,
  me: methods
}

