var token = md5(token2); //log(token)
var url = 'http://159.75.157.29:64/api.php/Smtv/vod/?ac=list&t='+MY_CLASS_URL+'&page='+MY_PAGE+'&token='+token+'&keytime='+t; //log(url) 
var html = request(url); //log(html)


eval(getCryptoJS())

var keyiv = 'yy577584751ylxue';
function decrypt(text) {
        let decrypted = CryptoJS.AES.decrypt(text, CryptoJS.enc.Utf8.parse(keyiv), {
            iv: CryptoJS.enc.Utf8.parse(keyiv),
            mode: CryptoJS.mode.CBC,
            padding: CryptoJS.pad.Pkcs7
        });
        return decrypted.toString(CryptoJS.enc.Utf8)
}

