eval(getCryptoJS())

function Decrypt(base64Str) {
    // 使用提供的解密秘钥
    var key = CryptoJS.enc.Utf8.parse("sWLUMtkR@TQ2Bfdx");

    // 将Base64字符串解码为字节数组
    var ciphertext = CryptoJS.enc.Base64.parse(base64Str);

    // 使用AES/ECB/PKCS5Padding模式进行解密
    var decrypted = CryptoJS.AES.decrypt({
        ciphertext: ciphertext
    }, key, {
        mode: CryptoJS.mode.ECB, // 使用ECB模式
        padding: CryptoJS.pad.Pkcs7 // 使用PKCS#7填充
    }).toString(CryptoJS.enc.Utf8);

    // 返回解密后的文本
    return decrypted;
}

