    '7': '日'
  };

  let timeDesc = '';

  if (x.show_month_day) {
    timeDesc += `${x.show_month_day}`;
  }

  if (x.show_week) {
    if (timeDesc) timeDesc += '/'; 
    timeDesc += `每周${weekMap[x.show_week] || x.show_week}`;
  }

  if (x.show_time) {
    if (timeDesc) timeDesc += '/';
    timeDesc += `${x.show_time}`;
  }
  desc += timeDesc;
  return desc.trim();
}
