    };

    Object.keys(fields).forEach(key => {
        if (data[key]) {
            // 如果键存在，且值是数组，使用 join 连接，否则直接赋值
            result[fields[key]] = Array.isArray(data[key]) ? data[key].join('/') : data[key];
        }
    });

    return result;
}
function transformData(data) {
    const descs = [];
    const titles = [];

    if (data["类型"]) descs.push(`类型：${data["类型"]}`);
