function getVideoDuration(videoUrl) {
    let retriever = new android.media.MediaMetadataRetriever();
    try {
        // 不要抓包会报错
        retriever.setDataSource(videoUrl, new java.util.HashMap());
        let time = retriever.extractMetadata(android.media.MediaMetadataRetriever.METADATA_KEY_DURATION);
        return Number(time);
    } catch (e) {
        log(e.toString());
        return 0;
    } finally {
        retriever.release();
    }
}
$.exports.load = function(vid, play, part, playurl, headers) {
    let millisecond = getVideoDuration(playurl) || 1000 * 60 * 20; //如果获取具体时长失败，就默认20分钟的弹幕
    let minute = Math.ceil(millisecond / 60000);
    let res = batchFetch(Array.from({length:minute},(v, i) => ({
        url: 'http://43.142.232.217:8090/app/danmu?vid=' + vid + '&play=' + play + '&part=' + part + '&start_time_point=' + (i * 60000) + '&end_time_point=' + (i + 1) * 60000 + '&limit=10&page=1',
        options: {
            headers
        }
    })));
    let count = 0;
    let data = "";
    for (let htmldm of res) {
        var items = JSON.parse(htmldm).data.items; //log(items.length)
        for (let i = 0; i < items.length; i++) {
            var time_point = items[i].time_point / 1000; //log(time_point)
            var contents = JSON.parse(items[i].content); //log(contents)
            var color = contents.color; //log(color)
            var content = contents.content; //log(content)

            tempdata = `<d p="${time_point},1,25,${color},0">${content}</d>\n`; //log(tempdata)
            data += tempdata;
            count++;
        }
    }
    data= `<d p="0,5,1,16711680,0">前方共有${count}条弹幕~</d>\n`+data;
    danmustr = `<?xml version="1.0" encoding="UTF-8"?>\n<i>\n${data}</i>`; //log(danmustr)
    writeFile('hiker://files/cache/danmu/Jong_danmu.xml', danmustr);
}

