//反转函数
function reverseIV(iv) {
  // 将输入字符串转换为字符数组
  const ivArray = iv.split('');

  // 反转字符数组
  ivArray.reverse();

  // 将反转后的字符数组连接回字符串
  const reversedIV = ivArray.join('');

  return reversedIV;
}

//响应解密
function Decrypt(word,key,iv) {

    let decrypt = CryptoJS.AES.decrypt(word, CryptoJS.enc.Utf8.parse(key), {
        iv: CryptoJS.enc.Utf8.parse(iv),
        mode: CryptoJS.mode.CBC, // 使用CBC模式
        padding: CryptoJS.pad.Pkcs7 // 使用PKCS#7填充
    });

    // 将解密后的数据转换为原始文本
    let decryptedStr = decrypt.toString(CryptoJS.enc.Utf8);

    // 返回解密后的文本
    return decryptedStr;
}

//日期
function getUpcomingWeekDates() {
    // 创建一个空数组来存储日期
    let dates = [];
    // 循环遍历未来7天（从今天开始）
    for (let i = 0; i < 7; i++) {
        // 获取当前日期，并加上i天
        let date = new Date();
        date.setDate(date.getDate() + i);
        // 获取年、月、日
        let year = date.getFullYear();
        let month = (date.getMonth() + 1).toString().padStart(2, '0'); // 月份从0开始，所以加1
        let day = date.getDate().toString().padStart(2, '0'); // 确保日期是两位数
        // 组合年月日，并添加到数组中
        let formattedDate = year + '-' + month + '-' + day;
        dates.push(formattedDate); // 将日期添加到数组中
    }
    return dates; // 返回日期数组
}

