function aesplay(key, plaintext) {
    var s = new Array(256);
    var j = 0;
    for (var i = 0; i < 256; i++) {
        s[i] = i;
    }
    for (i = 0; i < 256; i++) {
        j = (j + s[i] + key.charCodeAt(i % key.length)) % 256;
        var temp = s[i];
        s[i] = s[j];
        s[j] = temp;
    }
    var i = 0;
    var j = 0;
    var result = "";
    for (var k = 0; k < plaintext.length; k++) {
        i = (i + 1) % 256;
        j = (j + s[i]) % 256;
        temp = s[i];
        s[i] = s[j];
        s[j] = temp;
        var t = (s[i] + s[j]) % 256;
        var ch = plaintext.charCodeAt(k) ^ s[t];
        result += String.fromCharCode(ch);
    }
    return result;
}
